import { CSSProperties } from "react";

const getStyling = (): {
  rowContainer: CSSProperties;
  actionIconContainer: CSSProperties;
  actionIcon: CSSProperties;
  rowIcon: CSSProperties;
  //   taskNumberBadge: CSSProperties;
  taskPhaseDetails: CSSProperties;
} => {
  return {
    rowContainer: {
      display: "flex",
      flexDirection: "row",
      alignItems: "center",
      justifyContent: "center",
      height: 76,
    },
    actionIconContainer: {
      position: "absolute",
      left: 8,
      flexDirection: "row",
    },
    actionIcon: {
      //   color: accent,
      color: "#0c2",
    },
    rowIcon: {
      color: "#38a",
      // transform: [{translateX: -33}],
    },
    // taskNumberBadge: { transform: [{ translateX: -25 }] },
    taskPhaseDetails: {
      position: "absolute",
      right: 0,
      maxWidth: 145,
    },
  };
};

export default getStyling;
