import React, { FC } from "react";
import "./track.css";
import { useRow, useTrack } from "./util";
import { IPhaseDTO } from "../../interfaces";
import getStyling from "./style";

const Track = () => {
  const { phases } = useTrack();
  const styles = getStyling();
  // TODO::: Don't use IPhaseDTO here, it should be inner model => phaseDTO
  const Row = ({ phase }: { phase: IPhaseDTO }) => {
    const { id, name, description, number, icon, status } = phase;

    const {
      taskNumberBadgeStyleOverride,
      taskNumberBadgeNumberStyleOverride,
      taskPhaseDetailsHeight,
      taskPhaseDetailsStyleOverride,
      taskPhaseDetailsTextStyleOverride,
      taskTrackLineStyleOverride,
      taskIconStyleOverride,
    } = useRow(description, number, phases.length, status);

    return (
      <>
        {number === 1 ? (
          <div style={{ marginLeft: 20, alignSelf: "center" }}>H</div>
        ) : null}

        <div style={styles.rowContainer}>
          <View style={styles.actionIconContainer}>
            <Pressable
              onPress={() =>
                onEditClick(name, number, status, description, icon, id)
              }
            >
              <Icon
                iconType="FontAwesome5"
                iconName="pen"
                size={20}
                style={styles.actionIcon}
              />
            </Pressable>
            <Pressable
              style={{ marginLeft: 12 }}
              onPress={() => (id ? onDelete(id) : null)}
            >
              <Icon
                iconType="FontAwesome5"
                iconName="trash"
                size={20}
                style={styles.actionIcon}
              />
            </Pressable>
          </View>
          {icon ? (
            <Icon
              style={{ ...styles.rowIcon, ...taskIconStyleOverride }}
              size={30}
              iconType="Ionicons"
              iconName={icon}
            />
          ) : (
            <View style={{ width: 30 }} />
          )}
          <TaskNumberBadge
            onPress={updatePhaseStatus}
            phaseId={id ?? ""}
            containerStyle={{
              ...styles.taskNumberBadge,
              ...taskNumberBadgeStyleOverride,
            }}
            numberStyle={taskNumberBadgeNumberStyleOverride}
            number={number}
          />
          <Pressable
            style={styles.taskPhaseDetails}
            onPress={() =>
              expandPhaseDetails({
                name: name,
                description: description ?? "",
              })
            }
          >
            <TaskPhaseDetails
              hasLimitedDescriptionLines
              hasLimitedTitleLines
              title={name}
              containerStyle={{
                height: taskPhaseDetailsHeight,
                ...taskPhaseDetailsStyleOverride,
              }}
              description={description}
              textStyle={taskPhaseDetailsTextStyleOverride}
            />
          </Pressable>
        </div>
        <View style={styles.trackContainer}>
          <TaskTrackLine
            containerStyle={{
              ...taskTrackLineStyleOverride,
            }}
          />
          <Pressable
            style={{ marginLeft: 6, alignSelf: "center" }}
            onPress={() => onAddClick(number + 1)}
          >
            <Icon
              iconType="Ionicons"
              iconName="add-circle-outline"
              size={30}
              style={styles.actionIcon}
            />
          </Pressable>
        </View>
      </>
    );
  };
  return (
    <div className="track_container">
      <div className="track_body">
        {/* TODO::: Don't use IPhaseDTO here, it should be inner model => phaseDTO */}
        {phases.map((phase) => (
          <Row key={phase.id} phase={phase} />
        ))}
      </div>
    </div>
  );
};

export default Track;
