import React from "react";
import "./track.css";
import { useTrack } from "./util";
import { IPhaseDTO } from "../../interfaces";

const Track = () => {
  const { phases } = useTrack();

  const Row = ({ phase }: { phase: IPhaseDTO }) => {
    const isActive = phase.status > 0;

    return (
      <div className="track_row">
        <div className={`track_number ${isActive ? "" : "inactive"}`}>
          {phase.number}
        </div>
        <div className="track_content">
          <h3>{phase.name}</h3>
          <p>{phase.description}</p>
        </div>
      </div>
    );
  };

  return (
    <div className="track_container">
      <div className="track_body">
        {phases.map((phase) => (
          <Row key={phase.id} phase={phase} />
        ))}
      </div>
    </div>
  );
};

export default Track;
